package com.method5.jot.rpc;

import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.node.ArrayNode;
import com.fasterxml.jackson.databind.node.JsonNodeFactory;
import com.fasterxml.jackson.databind.node.TextNode;
import com.method5.jot.TestBase;
import com.method5.jot.crypto.Hasher;
import com.method5.jot.entity.Phase;
import com.method5.jot.events.EventRecord;
import com.method5.jot.extrinsic.ExtrinsicResult;
import com.method5.jot.query.ChainRpc;
import com.method5.jot.query.Query;
import com.method5.jot.query.StorageQuery;
import com.method5.jot.query.model.BlockHeader;
import com.method5.jot.query.model.SignedBlock;
import com.method5.jot.util.HexUtil;
import org.apache.commons.lang3.StringUtils;
import org.junit.jupiter.api.Test;
import org.mockito.MockedStatic;
import org.mockito.Mockito;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicReference;
import java.util.function.Consumer;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.Mockito.*;

public class WsApiTest extends TestBase {
    private static final ObjectMapper mapper = new ObjectMapper();

    private static final int TIMEOUT = 10000;

    @Test
    public void testCanCreateAndCloseWsClient() {
        try (PolkadotWs api = new PolkadotWs("ws://localhost:9944")) {
            assertNotNull(api);
        } catch (Exception e) {
            fail("Failed to initialize or close RPC client: " + e.getMessage());
        }
    }

    @Test
    public void testSendRequestHandlesConnectionFailureGracefully() {
        try (PolkadotWs api = new PolkadotWs("wss://invalid-endpoint")) {
            assertThrows(Exception.class, () -> {
                JsonNode emptyParams = new ObjectMapper().createArrayNode();
                api.send("system_chain", emptyParams);
            });
        }
    }

    @Test
    public void testConnectAndRetrieveData() {
        try (PolkadotWs api = new PolkadotWs(DOT_RPC_SERVERS, TIMEOUT)) {
            assertNotNull(api);
            Thread.sleep(100);

            JsonNode idNode = api.send("system_chain", JsonNodeFactory.instance.arrayNode());
            assertEquals("Polkadot", idNode.asText());
        } catch (Exception e) {
            fail("Failed to initialize or close RPC client: " + e.getMessage());
        }
    }

    @Test
    public void testRotateConnectionsAndRetrieveData() {
        String[] servers = new String[DOT_RPC_SERVERS.length + 1];
        servers[0] = "wss://invalid-endpoint";
        System.arraycopy(DOT_RPC_SERVERS, 0, servers, 1, DOT_RPC_SERVERS.length);

        try (PolkadotWs api = new PolkadotWs(servers, TIMEOUT)) {
            assertNotNull(api);
            Thread.sleep(100);

            JsonNode idNode = api.send("system_chain", JsonNodeFactory.instance.arrayNode());
            assertEquals("Polkadot", idNode.asText());
        } catch (Exception e) {
            fail("Failed to initialize or close RPC client: " + e.getMessage());
        }
    }

    @Test
    public void testSubscribe() {
        try (PolkadotWs api = new PolkadotWs(DOT_RPC_SERVERS, TIMEOUT)) {
            assertNotNull(api);
            Thread.sleep(100);

            CountDownLatch latch = new CountDownLatch(1);

            String subscriptionID = api.subscribe("chain_subscribeNewHeads", objectMapper.createArrayNode(), (JsonNode newHead) -> latch.countDown());

            if(!latch.await(6500, TimeUnit.MILLISECONDS)) {
                fail("Timeout waiting for subscribing new heads");
            }

            assertTrue(api.unsubscribe("chain_unsubscribeNewHead", subscriptionID));
        } catch (Exception e) {
            fail("Failed to initialize or close RPC client: " + e.getMessage());
        }
    }

    @Test
    public void testSubmitAndWaitSuccess() throws Exception {
        PolkadotWs api = mock(PolkadotWs.class);

        ArrayNode params = mapper.createArrayNode();
        params.add(HexUtil.bytesToHex(new byte[0]));

        when(api.send("author_submitExtrinsic", params)).thenReturn(new TextNode("00"));
        when(api.getChainSpec()).thenReturn(chainSpec);
        when(api.waitForExtrinsic("00", PolkadotWs.Confirmation.BEST, TIMEOUT)).thenCallRealMethod();
        when(api.waitForEvents(PolkadotWs.Confirmation.BEST, "00", TIMEOUT)).thenReturn(new ArrayList<>(List.of(new EventRecord(new Phase(Phase.Type.FINALIZATION, 0), "System", "ExtrinsicSuccess", null))));
        when(api.submitAndWaitForExtrinsic(new byte[0], PolkadotWs.Confirmation.BEST, TIMEOUT)).thenCallRealMethod();
        when(api.query()).thenReturn(new Query(api));

        ExtrinsicResult result = api.submitAndWaitForExtrinsic(new byte[0], PolkadotWs.Confirmation.BEST, TIMEOUT);
        assertTrue(result.isSuccess());
    }

    @Test
    public void testSubmitAndWaitFailure() throws Exception {
        PolkadotWs api = mock(PolkadotWs.class);

        ArrayNode params = mapper.createArrayNode();
        params.add(HexUtil.bytesToHex(new byte[0]));

        when(api.send("author_submitExtrinsic", params)).thenReturn(new TextNode("00"));
        when(api.getChainSpec()).thenReturn(chainSpec);
        when(api.waitForExtrinsic("00", PolkadotWs.Confirmation.BEST, TIMEOUT)).thenCallRealMethod();
        when(api.waitForEvents(PolkadotWs.Confirmation.BEST, "00", TIMEOUT)).thenReturn(new ArrayList<>(List.of(new EventRecord(new Phase(Phase.Type.FINALIZATION, 0), "System", "ExtrinsicFailed", new HashMap<>()))));
        when(api.submitAndWaitForExtrinsic(new byte[0], PolkadotWs.Confirmation.BEST, TIMEOUT)).thenCallRealMethod();
        when(api.query()).thenReturn(new Query(api));

        ExtrinsicResult result = api.submitAndWaitForExtrinsic(new byte[0], PolkadotWs.Confirmation.BEST, TIMEOUT);
        assertFalse(result.isSuccess());
    }

    @Test
    public void testWaitForEvents() throws Exception {
        PolkadotWs api = mock(PolkadotWs.class);
        when(api.getChainSpec()).thenReturn(chainSpec);
        when(api.getResolver()).thenReturn(resolver);
        when(api.getMetadata()).thenReturn(metadata);

        doCallRealMethod().when(api).waitForEvents(anyString(), anyLong());
        doCallRealMethod().when(api).waitForEvents(any(PolkadotWs.Confirmation.class), anyString(), anyLong());

        AtomicReference<Consumer<JsonNode>> onUpdateRef = new AtomicReference<>();

        when(api.subscribe(eq("chain_subscribeNewHeads"), any(ArrayNode.class), any()))
                .thenAnswer(inv -> {
                    @SuppressWarnings("unchecked")
                    Consumer<JsonNode> onUpdate = inv.getArgument(2);
                    onUpdateRef.set(onUpdate);
                    return "sub-1";
                });

        when(api.unsubscribe(eq("chain_unsubscribeNewHeads"), eq("sub-1"))).thenReturn(true);
        when(api.send(eq("chain_getBlockHash"), any(JsonNode.class))).thenReturn(new TextNode("0x00"));

        String eventHex = StringUtils.join("",
                "");
        when(api.send(eq("state_getStorage"), any(JsonNode.class))).thenReturn(new TextNode(eventHex));

        final String extrinsicHex = "0xfeedface";
        SignedBlock signedBlock = new SignedBlock();
        signedBlock.getBlock().setExtrinsics(List.of(extrinsicHex));
        when(api.send(eq("chain_getBlock"), any(JsonNode.class))).thenReturn(objectMapper.valueToTree(signedBlock));

        MockedStatic<SubscriptionFactory> subscriptionFactoryStatic = Mockito.mockStatic(SubscriptionFactory.class);
        @SuppressWarnings("unchecked")
        MessageParser<BlockHeader> parser = (MessageParser<BlockHeader>) mock(MessageParser.class);
        BlockHeader header = mock(BlockHeader.class);
        when(header.getNumber()).thenReturn(0);
        when(parser.parse(any(JsonNode.class))).thenReturn(header);
        subscriptionFactoryStatic.when(() -> SubscriptionFactory.getParser(SubscriptionType.BEST_HEAD))
                .thenReturn(parser);

        ChainRpc chainRpc = mock(ChainRpc.class);

        Query query = mock(Query.class);
        when(query.chain()).thenReturn(chainRpc);

        when(api.query()).thenReturn(query);

        when(chainRpc.blockHash(eq(0L))).thenReturn("00");
        when(chainRpc.block(anyString())).thenReturn(signedBlock);

        MockedStatic<Hasher> hasherStatic = Mockito.mockStatic(Hasher.class);
        final String targetExtrinsicHash = "98e558f757307660266164fa767a45f91ff7e0f9dbf7e381a048a56bacb778e6";
        hasherStatic.when(() -> Hasher.hash256(eq(HexUtil.hexToBytes(extrinsicHex))))
                .thenReturn(HexUtil.hexToBytes(targetExtrinsicHash));

        Phase phase = mock(Phase.class);
        when(phase.isApplyExtrinsic(anyInt())).thenAnswer(inv -> ((Integer) inv.getArgument(0)) == 0);

        EventRecord event = mock(EventRecord.class);
        when(event.phase()).thenReturn(phase);
        when(event.pallet()).thenReturn("System");
        when(event.method()).thenReturn("ExtrinsicSuccess");

        StorageQuery storageQuery = mock(StorageQuery.class);
        when(storageQuery.systemEvents(anyString())).thenReturn(List.of(event));
        when(query.storage()).thenReturn(storageQuery);

        AtomicReference<List<EventRecord>> resultRef = new AtomicReference<>();
        Thread t = new Thread(() -> {
            try {
                resultRef.set(api.waitForEvents(targetExtrinsicHash, TIMEOUT));
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        });
        t.start();

        while (onUpdateRef.get() == null) Thread.sleep(5);
        onUpdateRef.get().accept(new ObjectMapper().createObjectNode().put("dummy", 1));
        t.join();

        List<EventRecord> records = resultRef.get();
        assertNotNull(records);
        assertEquals(1, records.size());
        assertEquals("System", records.getFirst().pallet());
        assertEquals("ExtrinsicSuccess", records.getFirst().method());

        hasherStatic.close();
        subscriptionFactoryStatic.close();
    }
}
